    static func detectLanguage() -> String {
        let preferred = Locale.preferredLanguages.first ?? "en"
        let map: [String: String] = [
            "pt-BR": "pt", "pt-PT": "pp", "pt": "pt",
            "zh-Hans": "zh", "zh-Hant": "zt", "zh-TW": "zt", "zh-HK": "zt", "zh-CN": "zh", "zh": "zh",
            "es-419": "em", "nb": "no", "nn": "no",
        ]
        // Check full match first, then prefix
        if let mapped = map[preferred] { return mapped }
        let prefix = String(preferred.prefix(2))
        if let mapped = map[prefix] { return mapped }
        let codes = Set(supportedLangs.map { $0.code })
        if codes.contains(prefix) { return prefix }
        return "en"
    }

